execution.script:
node {
  env.REPO = "https://github.com/Vicentezapata/IADSO_CIISA_IECS_REPO_TEST.git"
  stage('Descargar Codigo (SCM)') {
    sh "echo ${WORKSPACE}"
    timeout(time: 3, unit: 'MINUTES'){
      input = input(
        id: 'userInput', messasge: 'Valores para PMV',
        parameters: [
          string(description: 'Nombre del proyecto', name: 'Name_Project'),
          string(description: 'Url de repositorio', name: 'Git_Url'),
          choice(
            description: 'Tipo de repositorio',
            choices: ['Publico', 'Privado'],
            name: 'Type_Repo'
          ),
          choice(
            description: 'Categorizacion',
            choices: ['WEBPAGE', 'MOBILE'],
            name: 'Type_Cat'
          )
        ]
      )
    }
    git input.Git_Url
  }

  stage('Control SAST') {
    // requires SonarQube Scanner 2.8+
    sh "**************************************************************************************************"
    sh "*                                     CONTROL SAST                                               *"
    sh "**************************************************************************************************"
    def scannerHome = tool 'sonarqube-scanner';//Mismo nombre que pusimos // en el global tool configuration
    withSonarQubeEnv('SonarQube') { // El nombre de servidor que //pusimos en Configuraci√≥n del sistema.
        sh "${scannerHome}/bin/sonar-scanner -Dsonar.projectKey='"+input.Name_Project.replace(" ","_")+"' -Dsonar.sources=. -Dsonar.java.binaries=."
    }
  }
  stage('Control DAST') {
    sh "**************************************************************************************************"
    sh "*                                     CONTROL DAST                                               *"
    sh "**************************************************************************************************"  
    def docker = tool 'docker';//Mismo nombre que pusimos // en el global tool configuration
      //sh "${docker}/bin/docker ps"
      sh "${docker}/bin/docker run -dit --name apache-app -p 8085:80 -v '/home/ciisa/Escritorio/Proyecto de titulo//jenkins-scripts/servidores_DAST/apache/':/usr/local/apache2/htdocs/ httpd:2.4"
      sh "${docker}/bin/docker pull aquasec/trivy:latest"
      sh "${docker}/bin/docker run --rm -v '/home/ciisa/Escritorio/Proyecto de titulo/trivy/trivy-cache/':/root/.cache/ aquasec/trivy:latest httpd:2.4 > '/opt/scripts/OutputTrivy.txt'"
      sh "rm -rf /opt/scripts/servidores_DAST/apache/Capacitatec-landing-page"
      sh "git clone ${REPO} /opt/scripts/servidores_DAST/apache/Capacitatec-landing-page"
      sh "*************************************************************" 
      sh "*************** Vulnerabilidades detectadas  ****************" 
      sh "*************************************************************" 
      sh "cat /opt/scripts/OutputTrivy.txt"
  }
}
